const PRICE={annually:{basic:["No",""],pro:["$499","/year"],business:["$999","/year"]},monthly:{basic:["Free",""],pro:["$49","/mo"],business:["$99","/mo"]}},planSwitcher=document.querySelector(".plan-switcher"),prices=document.querySelectorAll(".price");planSwitcher.addEventListener("click",(e=>{e.preventDefault();let t=e.target;if("BUTTON"===t.tagName){const e=planSwitcher.children;for(let t=0;t<e.length;t++)e[t].classList.remove("switch-on");t.classList.add("switch-on");let n=t.textContent.toLowerCase();for(const e of prices){let t=e.dataset.priceType;e.innerHTML=`${PRICE[n][t][0]}<span>${PRICE[n][t][1]}</span>`}}}));